mapper.select:
	select pwd 
	from tb_employee 
   		join tb_department using(dept_no) 
    	join tb_company using(cp_no)
	where cp_name = #{cp_name}
    	and email = #{email}
    	and name = #{name}

mapper.update:
update tb_forjoin
set random_num = (select round(dbms_random.value()*1000000)+1 as RandomNum from dual)
where email = #{email}

mapper.select:
select random_num 
from tb_forjoin
where email = #{email}
