mapper.delete:
delete from tb_project_favorite
	where pr_no = #{pr_no}
	and emp_no = #{emp_no}

delete from tb_project_board_fix
where pb_no = #{pb_no}
and emp_no = #{emp_no}

mapper.update:
update tb_project_emp
    set pr_emp_authority = #{pr_emp_authority}
    where pr_no = #{pr_no} and ep_no = #{emp_no}
