mapper.insert:
insert into tb_vacation(VACA_NO, EMP_NO
, VACA_SORT, VACA_START, VACA_END
, VACA_CNT, VACA_COMMENT, VACA_REQ_DATE
, VACA_CONFIRM, VACA_APPROVER, vaca_allday
) values(
(#{emp_no}||#{vaca_req_date}), #{emp_no}
, #{vaca_sort}, #{vaca_start}, #{vaca_end}
, #{vaca_cnt}, #{vaca_comment}, #{vaca_req_date}
, 1, 100, #{vaca_allday}
)

mapper.select:
select * 
from tb_vacation v join tb_vacation_use u on v.emp_no = u.emp_no
where emp_no = #{emp_no} and vaca_start between '#{vu_year}-01-01' and '#{vu_year}-12-31'

and vaca_confirm = #{vaca_confirm}


and vaca_sort = #{vaca_sort}
